mapper.select:
select m.id id, m.v7_u_module_name v7UModuleName, m.v7_u_feature_name v7UFeatureName, m.v7_u_feature_path v7UFeaturePath, m.v7_u_request_param v7URequestParam , m.v7_u_request_user v7URequestUser, m.v7_u_request_result v7URequestResult, m.v7_u_request_user v7URequestUser, m.v7_u_request_time v7URequestTime, m.v7_u_request_pcname v7URequestPcname, m.v7_u_request_ip v7URequestIp from V7_UNIVERSAL_LOG m
1=1
and m.v7_u_module_name like '%${moduleName}%'
and m.v7_u_feature_name like '%${featureName}%'
and m.v7_u_request_result like '%${requestResult}%'
and v7_u_request_time >= #{startTime}
and m.v7_u_request_time <= #{endTime}
order by m.v7_u_request_time DESC limit #{beginPageIndex}, #{pageSize}
